mapper.select:
SELECT step.id AS step_id, GROUP_CONCAT(scoreitem.item_name) AS score_item_list, scorevalue.player_id, scorevalue.judge_id, Sum(scorevalue.num_value) AS sum_score, player.`name`, player.company, player.dep, player.post, step.title, judge.judge_name FROM scorevalue INNER JOIN scoreitem ON scorevalue.scoreitem_id = scoreitem.id INNER JOIN step ON scoreitem.step_id = step.id INNER JOIN player ON scorevalue.player_id = player.id INNER JOIN judge ON scorevalue.judge_id = judge.id
AND scorevalue.judge_id = #{judgeid}
AND step.id = #{stepid}
AND player.`name` LIKE '%${playername}%'
AND scorevalue.player_id = #{playerid}
GROUP BY scorevalue.judge_id, scorevalue.player_id ORDER BY sum_score DESC

SELECT step.id AS step_id, scoreitem.item_name, scorevalue.player_id, scorevalue.judge_id, scoreitem.id AS scoreitem_id, scorevalue.num_value, scoreitem.max_score, player.`name`, player.company, player.dep, player.post, step.title, judge.judge_name FROM scorevalue INNER JOIN scoreitem ON scorevalue.scoreitem_id = scoreitem.id INNER JOIN step ON scoreitem.step_id = step.id INNER JOIN player ON scorevalue.player_id = player.id INNER JOIN judge ON scorevalue.judge_id = judge.id
AND scorevalue.judge_id = #{judgeid}
AND scorevalue.player_id = #{playerid}
AND scoreitem.step_id = #{stepid}
